                this.onFilterChange("*");
              }}
            >
              All
            </div>
            <div
              data-filter="brand"
              className={`work_portfolio_item ${this.onActive("web")}`}
              onClick={() => {
                this.onFilterChange("brand");
              }}
            >
              Branding
            </div>
            <div
              data-filter="develop"
              className={`work_portfolio_item ${this.onActive("develop")}`}
              onClick={() => {
                this.onFilterChange("develop");
              }}
            >
              Development
            </div>
            <div
              data-filter="web"
              className={`work_portfolio_item ${this.onActive("web")}`}
              onClick={() => {
                this.onFilterChange("web");
              }}
            >
              Web Design
            </div>
            <div
              data-filter="fashion"
              className={`work_portfolio_item ${this.onActive("fashion")}`}
              onClick={() => {
                this.onFilterChange("fashion");
              }}
            >
              Fashion
            </div>
            <div
              data-filter="ux"
              className={`work_portfolio_item ${this.onActive("ux")}`}
              onClick={() => {
                this.onFilterChange("ux");
              }}
            >
              UI/UX
            </div>
            <div
              data-filter="product"
              className={`work_portfolio_item ${this.onActive("product")}`}
              onClick={() => {
                this.onFilterChange("product");
              }}
            >
              Product
            </div>
          </div> */}
          <div className="row portfolio_gallery mb_30" id="work-portfolio">
            <Portfolio2griditem
              colClass="col-lg-4 col-sm-6 web fashion"
              pImg="Home.jpg"
              ptitle="Apple Mobile Mockup"
              tOne="Web"
              tTwo="Fashion"
            />
            <Portfolio2griditem
              colClass="col-lg-4 col-sm-6 develop"
              pImg="House_Details.jpg"
              ptitle="Interior Design"
              tOne="Develop"
            />
            <Portfolio2griditem
              colClass="col-lg-4 col-sm-6 web"
              pImg="House_Map_Details.jpg"
              ptitle="Portfolio Center Slider"
              tOne="Web"
              tTwo="Fashion"
            />
            <Portfolio2griditem
              colClass="col-lg-4 col-sm-6 fashion"
              pImg="Map_House.jpg"
              ptitle="Portfolio Masonry"
              tOne="fashion"
            />
            <Portfolio2griditem
              colClass="col-lg-4 col-sm-6 ux brand"
              pImg="3d_Virtual_Tour.jpg"
              ptitle="Double Exposure"
              tOne="UX"
              tTwo="Brand"
            />
            <Portfolio2griditem
              colClass="col-lg-4 col-sm-6 product"
              pImg="Choose_Location.jpg"
              ptitle="American Burger"
              tOne="Product"
            />
            <Portfolio2griditem
              colClass="col-lg-4 col-sm-6 develop fashion"
              pImg="Take_Photo.jpg"
              ptitle="Take Photo"
              tOne="From your camera"
              tTwo="From Your Gallery"
            />
            <Portfolio2griditem
              colClass="col-lg-4 col-sm-6 brand ux"
              pImg="Preferences.jpg"
              ptitle="Preferences"
              tOne="Sorting & "
              tTwo="Filtering"
            />
            {/* <Portfolio2griditem
              colClass="col-lg-4 col-sm-6 brand web"
              pImg="3-columns/3_col_9.jpg"
              ptitle="Portfolio Masonry"
              tOne="Brand"
              tTwo="UX"
            />
            <Portfolio2griditem
              colClass="col-lg-4 col-sm-6 develop"
              pImg="3-columns/3_col_10.jpg"
              ptitle="Interior Design"
              tOne="Develop"
            />
            <Portfolio2griditem
              colClass="col-lg-4 col-sm-6 fashion"
              pImg="3-columns/3_col_11.jpg"
              ptitle="Portfolio Masonry"
              tOne="fashion"
            />
            <Portfolio2griditem
              colClass="col-lg-4 col-sm-6 develop fashion"
              pImg="3-columns/3_col_12.jpg"
              ptitle="Double Exposure"
              tOne="Develop"
              tTwo="Fashion"
            /> */}
          </div>
        </div>
      </section>
    );
  }
}
export default Portfolio3grid;
